import React, { useState } from 'react';
import { 
  Facebook, 
  Twitter, 
  Instagram, 
  Mail, 
  Phone, 
  MapPin,
  ChevronUp,
  ExternalLink 
} from 'lucide-react';

const Footer = () => {
  const [emailSubscribe, setEmailSubscribe] = useState('');
  const [showTooltip, setShowTooltip] = useState('');
  const [expandedSection, setExpandedSection] = useState('');

  const handleSubscribe = (e) => {
    e.preventDefault();
    // Simulate subscription
    if (emailSubscribe) {
      alert('Thanks for subscribing! Check your email to confirm.');
      setEmailSubscribe('');
    }
  };

  const handleSocialHover = (platform) => {
    setShowTooltip(platform);
  };

  const toggleSection = (section) => {
    setExpandedSection(expandedSection === section ? '' : section);
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <footer className="bg-gray-900 text-gray-300 py-12 relative">
      <div className="max-w-6xl mx-auto px-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {/* About Section */}
          <div className="space-y-4">
            <h4 className="text-xl font-bold text-white flex items-center justify-between">
              About Us
              <ChevronUp 
                className={`h-5 w-5 md:hidden cursor-pointer transform transition-transform ${
                  expandedSection === 'about' ? 'rotate-180' : ''
                }`}
                onClick={() => toggleSection('about')}
              />
            </h4>
            <div className={`space-y-3 ${
              expandedSection === 'about' || window.innerWidth >= 768 ? 'block' : 'hidden'
            }`}>
              <p className="text-sm leading-relaxed">
                We are dedicated to providing innovative solutions with a focus on 
                excellence and customer satisfaction. Our commitment to quality 
                drives everything we do.
              </p>
              <div className="flex items-center space-x-2 text-sm">
                <MapPin className="h-4 w-4" />
                <span>123 Innovation Street, Tech City</span>
              </div>
            </div>
          </div>

          {/* Quick Links */}
          <div className="space-y-4">
            <h4 className="text-xl font-bold text-white flex items-center justify-between">
              Quick Links
              <ChevronUp 
                className={`h-5 w-5 md:hidden cursor-pointer transform transition-transform ${
                  expandedSection === 'links' ? 'rotate-180' : ''
                }`}
                onClick={() => toggleSection('links')}
              />
            </h4>
            <nav className={`${
              expandedSection === 'links' || window.innerWidth >= 768 ? 'block' : 'hidden'
            }`}>
              <ul className="space-y-2">
                {['Home', 'Services', 'Projects', 'Blog', 'Contact'].map((item) => (
                  <li key={item}>
                    <a 
                      href={`#${item.toLowerCase()}`}
                      className="hover:text-white transition-colors duration-200 flex items-center group"
                    >
                      <span>{item}</span>
                      <ExternalLink className="h-3 w-3 ml-1 opacity-0 group-hover:opacity-100 transition-opacity" />
                    </a>
                  </li>
                ))}
              </ul>
            </nav>
          </div>

          {/* Contact Section */}
          <div className="space-y-4">
            <h4 className="text-xl font-bold text-white flex items-center justify-between">
              Contact Us
              <ChevronUp 
                className={`h-5 w-5 md:hidden cursor-pointer transform transition-transform ${
                  expandedSection === 'contact' ? 'rotate-180' : ''
                }`}
                onClick={() => toggleSection('contact')}
              />
            </h4>
            <div className={`space-y-3 ${
              expandedSection === 'contact' || window.innerWidth >= 768 ? 'block' : 'hidden'
            }`}>
              <div className="flex items-center space-x-2">
                <Phone className="h-4 w-4" />
                <span>+1 (555) 123-4567</span>
              </div>
              <div className="flex items-center space-x-2">
                <Mail className="h-4 w-4" />
                <span>contact@example.com</span>
              </div>
            </div>
          </div>

          {/* Newsletter Section */}
          <div className="space-y-4">
            <h4 className="text-xl font-bold text-white flex items-center justify-between">
              Newsletter
              <ChevronUp 
                className={`h-5 w-5 md:hidden cursor-pointer transform transition-transform ${
                  expandedSection === 'newsletter' ? 'rotate-180' : ''
                }`}
                onClick={() => toggleSection('newsletter')}
              />
            </h4>
            <div className={`space-y-3 ${
              expandedSection === 'newsletter' || window.innerWidth >= 768 ? 'block' : 'hidden'
            }`}>
              <p className="text-sm">Subscribe to our newsletter for updates and exclusive offers.</p>
              <form onSubmit={handleSubscribe} className="space-y-2">
                <input
                  type="email"
                  value={emailSubscribe}
                  onChange={(e) => setEmailSubscribe(e.target.value)}
                  placeholder="Enter your email"
                  className="w-full px-3 py-2 bg-gray-800 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 text-white"
                  required
                />
                <button
                  type="submit"
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded transition-colors duration-200"
                >
                  Subscribe
                </button>
              </form>
            </div>
          </div>
        </div>

        {/* Social Links */}
        <div className="mt-8 pt-8 border-t border-gray-800">
          <div className="flex justify-center space-x-6">
            {[
              { icon: Facebook, name: 'Facebook', link: 'https://facebook.com' },
              { icon: Twitter, name: 'Twitter', link: 'https://twitter.com' },
              { icon: Instagram, name: 'Instagram', link: 'https://instagram.com' }
            ].map(({ icon: Icon, name, link }) => (
              <a
                key={name}
                href={link}
                className="relative hover:text-white transition-colors duration-200"
                onMouseEnter={() => handleSocialHover(name)}
                onMouseLeave={() => handleSocialHover('')}
                target="_blank"
                rel="noopener noreferrer"
              >
                <Icon className="h-6 w-6" />
                {showTooltip === name && (
                  <span className="absolute -top-8 left-1/2 transform -translate-x-1/2 bg-gray-800 px-2 py-1 rounded text-xs">
                    {name}
                  </span>
                )}
              </a>
            ))}
          </div>
        </div>

        {/* Copyright */}
        <div className="mt-8 text-center text-sm">
          <p>&copy; {new Date().getFullYear()} Your Company. All rights reserved.</p>
        </div>

        {/* Scroll to top button */}
        <button
          onClick={scrollToTop}
          className="fixed bottom-8 right-8 bg-blue-600 hover:bg-blue-700 text-white p-2 rounded-full shadow-lg transition-colors duration-200"
          aria-label="Scroll to top"
        >
          <ChevronUp className="h-6 w-6" />
        </button>
      </div>
    </footer>
  );
};

export default Footer;
